import { useState } from 'react';

import { Main, Container, Nav } from './styles.jsx';

const axios = require('axios');

function App() {

  let [userName, setUserName] = useState();
  let [repositorios, setRepositorios] = useState(null);

  function pesquisaUsuario()
  {
    if (userName !== '')
    {
      console.log('ok')

      axios.get(`https://api.github.com/users/${userName}/repos`)
      .then((resposta) => {
        repositorios = resposta.data;
      }).catch(function()
      {
        let mensagem = 'Houve um erro';
      })
    }
  }

  return (
    <div className="App">
      <Main>
        <Container>
          <header>
            <Nav>
              <a>
                <img src='https://hexalab.com.br/n/static/media/hexalab-icon.33463a0b.svg' alt='HexaLab - Logo' />
              </a>
            </Nav>
          </header>

          <form>
            <input
              placeholder="ok"
              onChange={(ev) => {
                let inputValue = ev.target.value;

                setUserName(inputValue);
              }}
            />

            <button
              onClick={(ev) => {
                ev.preventDefault();

                pesquisaUsuario();
              }}
            >
              Pesquisar
            </button>
          </form>

          <section id='repositorios'>
              {
                repositorios.map((value, i) => {
                  /*
                  return(
                    <div key={i}>
                      {value}
                    </div>
                  )*/
                  console.log(value);
                })
              }
          </section>
        </Container>
      </Main>
    </div>
  );
}

export default App;
