import { useState } from 'react';

import './styles/index.css';

import { Main, Nav } from './styles.jsx';

const axios = require('axios');

function App() {

  let [userName, setUserName] = useState();

  let [estadoRequisicao, setEstadoRequisicao] = useState();
  let [repositorios, setRepositorio] = useState([]);

  function pesquisaUsuario()
  {
    if (userName !== '')
    {
      axios.get(`https://api.github.com/users/${userName}/repos`)
      .then((resposta) => {
        setRepositorio(resposta.data);
        setEstadoRequisicao('Requisicao feita com sucesso');
      }).catch(function()
      {
        setEstadoRequisicao('Houve um erro');
      })
    }
  }

  function retornaRepositorios()
  {
    //{
      repositorios.map((value, key) => {
        return(
          <div key={key}>
            <a href={value.html_url}>
              {value.name}
            </a>
          </div>
        );
      })

  }

  return (
    <div className="App">
      <Main>
        <section id='informacoes'>
          <header>
            <Nav>
              <a>
                <img src='https://hexalab.com.br/n/static/media/hexalab-icon.33463a0b.svg' alt='HexaLab - Logo' />
              </a>
            </Nav>
          </header>

          <form>
            <input
              placeholder="ok"
              onChange={(ev) => {
                let inputValue = ev.target.value;

                setUserName(inputValue);
              }}
            />

            <button
              onClick={(ev) => {
                ev.preventDefault();

                pesquisaUsuario();
              }}
            >
              Pesquisar
            </button>
          </form>
        </section>

        <section id='repositorios'>
            {
              retornaRepositorios()
            }
        </section>
      </Main>
    </div>
  );
}

export default App;
